import os
import gradio as gr
import numpy as np

def combine(a, b):
    return a + " " + b


def mirror(x):
    return x


with gr.Blocks() as app:
    with gr.Row():
        txt = gr.Textbox(label="Input", value="Gradio makes me")
        txt_2 = gr.Textbox(label="Second", value="mind blowing")
    btn = gr.Button(value="Bind")
    txt_3 = gr.Textbox(value="", label="Output", lines=2)
    btn.click(combine, inputs=[txt, txt_2], outputs=[txt_3])

    with gr.Row():
        im = gr.Image()
        im_2 = gr.Image()

    btn = gr.Button(value="Mirror Image")
    btn.click(mirror, inputs=[im], outputs=[im_2])

    gr.Markdown("## Text Examples")
    gr.Examples(
        [["hello", "foo"], ["world", "bar"]],
        [txt, txt_2],
        txt_3,
        combine,
        cache_examples=True,
    )
    gr.Markdown("## Image Examples")
    # create a temp file to decode base64 data as further examples image
    mindblowing = gr.Image(visible=False)
    path = mindblowing.base64_to_temp_file_if_needed(
        "data:image/jpeg;base64,",
        "mindblowing.jpg"
    )
    gr.Examples(
        examples=[path],
        inputs=im,
        outputs=im_2,
        fn=mirror,
        cache_examples=True,
    )

if __name__ == "__main__":
    app.launch(
        share=False,
        debug=False,
        server_name="0.0.0.0", # establish on localhost available IPs
        server_port=8000,
        )

